!function(e){
	"use strict";
	e('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function(){
		if(location.pathname.replace(/^\//,"") == this.pathname.replace(/^\//,"") && location.hostname == this.hostname){
			var a = e(this.hash);
			if((a = a.length ? a : e("[name="+this.hash.slice(1)+"]")).length)
				return e("html, body").animate({scrollTop:a.offset().top-70},1e3,"easeInOutExpo"),!1
		}
	}),
	e(".js-scroll-trigger").click(function(){
		e(".navbar-collapse").collapse("hide")
	}),
	e("body").scrollspy({
		target:"#mainNav",offset:100
	});
	function a(){
		100 < e("#mainNav").offset().top ? e("#mainNav").addClass("navbar-shrink") : e("#mainNav").removeClass("navbar-shrink")
	}
	a(), e(window).scroll(a)
}(jQuery);


// ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
/* Modals */

// Resume
// Get the modal
var resumeModal = document.getElementById("resumeModal");

// Get the button that opens the modal
var resumeBtn = document.getElementById("resumeBtn");

// Get the <span> element that closes the modal
var resumeClose = document.getElementById("resumeClose");

// When the user clicks on the button, open the modal
resumeBtn.onclick = function() {
	resumeModal.style.display = "block";
}

// When the user clicks on <span> (x), close the modal
resumeClose.onclick = function() {
	resumeModal.style.display = "none";
}

// When the user clicks anywhere outside of the modal, close it
window.onclick = function(event) {
	if (event.target == resumeModal) {
		resumeModal.style.display = "none";
	}
}

// Hide/show the descriptions based on the button push
// references:
// https://stackoverflow.com/questions/20306204/using-queryselector-with-ids-that-are-numbers
// https://stackoverflow.com/questions/7815374/get-element-inside-element-by-class-and-id-javascript
function readMore(projectid) {
	let dots = document.getElementById(projectid).getElementsByClassName("dots");
    let moreText = document.getElementById(projectid).getElementsByClassName("more");
    let btnText = document.getElementById(projectid).getElementsByClassName("btn btn-primary")[0];

    if (dots[0].style.display === "none") {
		for(i=0; i < dots.length;i++)
			dots[i].style.display = "inline";
        btnText.textContent = "Learn more";
		for(i=0; i<moreText.length;i++)
			moreText[i].style.display = "none";
    } else {
        for(i=0; i < dots.length;i++)
			dots[i].style.display = "none";
        btnText.textContent = "Show Less"; 
        for(i=0; i<moreText.length;i++)
			moreText[i].style.display = "inline";
    }
} 
